<div class="min-h-screen flex flex-col sm:justify-center items-center pt-6 sm:pt-0 bg-gray-100 dark:bg-gray-900"
     x-data="{
         darkMode: false,
         initTheme() {
             const stored = localStorage.getItem('darkMode');
             const prefersDark = window.matchMedia('(prefers-color-scheme: dark)').matches;
             this.darkMode = stored === 'true' || (!stored && prefersDark);
             this.updateTheme();
         },
         toggleTheme() {
             this.darkMode = !this.darkMode;
             this.updateTheme();
         },
         updateTheme() {
             localStorage.setItem('darkMode', this.darkMode);
             if (this.darkMode) {
                 document.documentElement.classList.add('dark');
             } else {
                 document.documentElement.classList.remove('dark');
             }
         }
     }"
     x-init="initTheme()">
    <div class="absolute top-4 right-4 z-10">
        <button @click="toggleTheme()" 
                class="p-2 rounded-lg bg-white dark:bg-gray-800 shadow-md hover:shadow-lg transition-shadow focus:outline-none focus:ring-2 focus:ring-indigo-500 relative"
                aria-label="Alternar tema">
            <div class="relative w-5 h-5">
                <!-- Ícone de lua (mostra quando está em modo claro - clicar ativa modo escuro) -->
                <svg x-show="!darkMode" 
                     x-transition:enter="transition ease-out duration-200"
                     x-transition:enter-start="opacity-0 scale-75"
                     x-transition:enter-end="opacity-100 scale-100"
                     x-transition:leave="transition ease-in duration-150"
                     x-transition:leave-start="opacity-100 scale-100"
                     x-transition:leave-end="opacity-0 scale-75"
                     class="absolute inset-0 w-5 h-5 text-gray-700 dark:text-gray-300" 
                     fill="none" 
                     stroke="currentColor" 
                     viewBox="0 0 24 24"
                     xmlns="http://www.w3.org/2000/svg">
                    <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M20.354 15.354A9 9 0 018.646 3.646 9.003 9.003 0 0012 21a9.003 9.003 0 008.354-5.646z" />
                </svg>
                <!-- Ícone de sol (mostra quando está em modo escuro - clicar ativa modo claro) -->
                <svg x-show="darkMode" 
                     x-transition:enter="transition ease-out duration-200"
                     x-transition:enter-start="opacity-0 scale-75"
                     x-transition:enter-end="opacity-100 scale-100"
                     x-transition:leave="transition ease-in duration-150"
                     x-transition:leave-start="opacity-100 scale-100"
                     x-transition:leave-end="opacity-0 scale-75"
                     class="absolute inset-0 w-5 h-5 text-gray-700 dark:text-gray-300" 
                     fill="none" 
                     stroke="currentColor" 
                     viewBox="0 0 24 24"
                     xmlns="http://www.w3.org/2000/svg">
                    <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M12 3v1m0 16v1m9-9h-1M4 12H3m15.364 6.364l-.707-.707M6.343 6.343l-.707-.707m12.728 0l-.707.707M6.343 17.657l-.707.707M16 12a4 4 0 11-8 0 4 4 0 018 0z" />
                </svg>
            </div>
        </button>
    </div>
